import React from 'react';


class GreetingForm extends React.Component{
  constructor(props){
    super(props);

    this.textInput = null;

    this.setTextInputRef = element => {
      this.textInput = element;
    };

    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(event) {
    event.preventDefault();
    console.log('this is input text: ' + this.textInput.value);

    var name = this.textInput.value;

    if (name.length > 0) {
      this.textInput.value = '';
      this.props.onNewName(name);
    }
    // console.log('A name was submitted: ' + this.state.value);
    // <input type="text" ref={this.setTextInputRef} />
  }

  render(){
    return(
      <form onSubmit={this.handleSubmit}>
        <h3>Write one or more IBAN to check them on validity</h3>
        <p style="grey">Example: EE38 2200 2210 2014 5685, GB98MIDL07009312345678, IE12BOFI 9000011234568 </p>
        <textarea ref={this.setTextInputRef} rows="4" cols="50">
        Write here one or more IBAN separated with coma or line break
        </textarea>
        <p></p>
        <button>Check IBAN</button>
      </form>
    )
  }
}

export default GreetingForm;
