import React from 'react';
import GreetingService from '../services/GreetingService';
import IbanValidationResult from './IbanValidationResult';
import IbanInputForm from './IbanInputForm';

class IbanValidationApp extends React.Component{
  constructor(props){
    super(props)
    this.state = {
      ibans: '', nameIsSet: false
    }
    this.handleNewName = this.handleNewName.bind(this);
    this.handleBackClick = this.handleBackClick.bind(this);
  }

  handleNewName(name){
    console.log('handleNewName called');
    this.setState({
      nameIsSet: true
    })
    this.setIbanValidationResult(name);
  }

  componentDidMount(){
    this.setIbanValidationResult('');
  }

  setIbanValidationResult(name){
    console.log('setIbanValidationResult called');
    if(name.length){
      GreetingService.getGreeting(name).then((response) => {
        this.setState({
          ibans: response.data
        })
      });
    }
  }

  handleBackClick() {
    this.setState({
      nameIsSet: false
    })
  }
  // [<IbanValidationResult message={this.state.ibans}/>,

  render(){
    const nameIsSet = this.state.nameIsSet;
    console.log('ibans value: ' + JSON.stringify(this.state.ibans));
    return(
      <div>
        {nameIsSet
          ? [<IbanValidationResult ibanStates={this.state.ibans}/>,
              <button onClick={this.handleBackClick}>Back</button>]
          : [<h1>IBAN CHECKER </h1>,
              <IbanInputForm onNewName={this.handleNewName}/>]
        }
      </div>
    )
  }
}
export default IbanValidationApp;
